using Tamir.SharpSsh;\nusing Tamir.SharpSsh.jsch;\nusing System;\nusing System.IO;\n\ntry\n{\n    using (SshTransferProtocolBase scp = new Scp(SSH_HOST, SSH_USER))\n    {\n        scp.Password = SSH_PASSWORD;\n        scp.Connect();\n\n        foreach (string file in files)\n        {\n            try\n            {\n                string remotePath = "incoming/" + new FileInfo(file).Name;\n                scp.Put(file, remotePath);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error uploading file {file}: {ex.Message}");\n                // Optionally, add more specific error handling or retry logic here\n            }\n        }\n    } // scp.Close() will be called automatically here\n}\ncatch (Exception ex)\n{\n    Console.WriteLine($"Connection error: {ex.Message}");\n    // Handle connection errors\n}