using System;\nusing System.IO;\nusing NHibernate;\n\npublic class ResumeRepository\n{\n    private readonly ISessionFactory sessionFactory;\n\n    public ResumeRepository(ISessionFactory sessionFactory)\n    {\n        this.sessionFactory = sessionFactory;\n    }\n\n    public void SaveResume(int userId, string fileName, byte[] fileContent)\n    {\n        using (var session = sessionFactory.OpenSession())\n        using (var transaction = session.BeginTransaction())\n        {\n            var resume = new Resume\n            {\n                UserId = userId,\n                FileName = fileName,\n                Content = fileContent\n            };\n\n            session.Save(resume);\n            transaction.Commit();\n        }\n    }\n}\n\n// Usage example\npublic void UploadResume(int userId, string filePath)\n{\n    byte[] fileContent = File.ReadAllBytes(filePath);\n    string fileName = Path.GetFileName(filePath);\n\n    var sessionFactory = // Initialize your NHibernate session factory\n    var repository = new ResumeRepository(sessionFactory);\n    repository.SaveResume(userId, fileName, fileContent);\n}