public class SomeDataClass : IDisposable\n{\n    private SqlConnection _conn;\n\n    // Constructors and methods\n\n    private void DoSomethingWithTheSqlConnection()\n    {\n        // some code excluded for brevity\n\n        // Ensure the connection is properly managed with using statement\n        using (SqlConnection sqlConnection = new SqlConnection(_conn.ConnectionString))\n        {\n            try\n            {\n                using (SqlCommand cmd = new SqlCommand(SqlQuery.CountSomething, sqlConnection))\n                {\n                    sqlConnection.Open();\n                    int countOfSomething = Convert.ToInt32(cmd.ExecuteScalar());\n                }\n            }\n            catch (Exception ex)\n            {\n                // Handle exception if necessary\n                throw; // or log the exception\n            }\n        }\n\n        // some code excluded for brevity\n    }\n\n    public void Dispose()\n    {\n        if (_conn != null)\n        {\n            _conn.Dispose();\n        }\n    }\n}