partial class MyFunkyDataTable\n{\n    protected override void OnColumnChanged(System.Data.DataColumnChangeEventArgs e)\n    {\n        base.OnColumnChanged(e);\n        if (e.Column.ColumnName == "DO_NOT_EST_FLAG")\n        {\n            e.Row["ESTIMATION_ALLOWED"] = ((MyFunkyRow)e.Row).ESTIMATION_ALLOWED;\n        }\n    }\n\n    protected override void OnRowChanged(System.Data.DataRowChangeEventArgs e)\n    {\n        base.OnRowChanged(e);\n        if (e.Action == DataRowAction.Add || e.Action == DataRowAction.Change)\n        {\n            ((MyFunkyRow)e.Row)["ESTIMATION_ALLOWED"] = ((MyFunkyRow)e.Row).ESTIMATION_ALLOWED;\n        }\n    }\n}