using System;\nusing System.Net.Mail;\nusing System.Threading;\n\npublic class EmailSender\n{\n    public void SendEmailWithRetry(string toAddress, string subject, string body)\n    {\n        int retryCount = 3;\n        int delay = 5000; // 5 seconds\n        bool emailSent = false;\n\n        for (int i = 0; i < retryCount; i++)\n        {\n            try\n            {\n                using (SmtpClient client = new SmtpClient("smtp.example.com"))\n                {\n                    client.Timeout = 5000; // 5 seconds\n                    MailMessage mailMessage = new MailMessage();\n                    mailMessage.From = new MailAddress("no-reply@example.com");\n                    mailMessage.To.Add(toAddress);\n                    mailMessage.Subject = subject;\n                    mailMessage.Body = body;\n\n                    client.Send(mailMessage);\n                    emailSent = true;\n                    break; // Exit loop if email is sent successfully\n                }\n            }\n            catch (SmtpException ex)\n            {\n                // Log the exception (use a proper logging framework in a real application)\n                Console.WriteLine($"Attempt {i + 1} failed: {ex.Message}");\n                Thread.Sleep(delay); // Wait before retrying\n            }\n        }\n\n        if (!emailSent)\n        {\n            // Log or notify sys admin that the email could not be sent after retries\n            Console.WriteLine("Email could not be sent after multiple attempts. Notify sys admin.");\n        }\n    }\n}