using System;\n\npublic class DateTimeHelper\n{\n    public static DateTime ConvertUtcToLocal(DateTime utcDateTime, string timeZoneId)\n    {\n        // Find the time zone by id\n        TimeZoneInfo timeZoneInfo = TimeZoneInfo.FindSystemTimeZoneById(timeZoneId);\n        \n        // Convert the UTC time to the specified time zone\n        DateTime localDateTime = TimeZoneInfo.ConvertTimeFromUtc(utcDateTime, timeZoneInfo);\n        \n        return localDateTime;\n    }\n}\n\n// Example usage\npublic class Program\n{\n    public static void Main()\n    {\n        DateTime utcDateTime = DateTime.UtcNow;\n        string timeZoneId = "Eastern Standard Time";  // Example time zone ID\n\n        DateTime localDateTime = DateTimeHelper.ConvertUtcToLocal(utcDateTime, timeZoneId);\n\n        Console.WriteLine("UTC Time: " + utcDateTime);\n        Console.WriteLine("Local Time: " + localDateTime);\n    }\n}