using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        string url = "http://ws.audioscrobbler.com/2.0/?method=artist.getinfo&artist=Cher&api_key=YOUR_API_KEY&format=xml";\n        \n        using (HttpClient client = new HttpClient())\n        {\n            try\n            {\n                // Fetch the response as a string\n                string responseString = await client.GetStringAsync(url);\n\n                // Load the string into an XDocument\n                XDocument xDocument = XDocument.Parse(responseString);\n\n                // Optionally, you can print the XML to verify it's correct\n                Console.WriteLine(xDocument);\n\n                // Now you can use LINQ to work with the XDocument\n                // Example: Get artist name\n                var artistName = xDocument.Root.Element("artist")?.Element("name")?.Value;\n                Console.WriteLine($"Artist Name: {artistName}");\n            }\n            catch (HttpRequestException e)\n            {\n                Console.WriteLine($"Request error: {e.Message}");\n            }\n            catch (ArgumentException e)\n            {\n                Console.WriteLine($"Argument error: {e.Message}");\n            }\n            catch (Exception e)\n            {\n                Console.WriteLine($"Unexpected error: {e.Message}");\n            }\n        }\n    }\n}