public class Configuration\n{\n    public string Name { get; set; }\n    public string Value { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<Configuration> configurations = new List<Configuration>\n        {\n            new Configuration { Name = "myConfig", Value = "someValue" },\n            new Configuration { Name = "otherConfig", Value = "otherValue" }\n            // Add more configurations as needed\n        };\n\n        string searchName = "myConfig";\n        Configuration foundConfig = null;\n\n        foreach (Configuration config in configurations)\n        {\n            if (config.Name == searchName)\n            {\n                foundConfig = config;\n                break;\n            }\n        }\n\n        if (foundConfig != null)\n        {\n            Console.WriteLine($"Found configuration: Name = {foundConfig.Name}, Value = {foundConfig.Value}");\n        }\n        else\n        {\n            Console.WriteLine("Configuration not found.");\n        }\n    }\n}