using System;\nusing System.Security.Cryptography;\nusing System.Xml;\n\npublic class TripleDESKeyStorage\n{\n    public void LoadKeyAndIV(TripleDESCryptoServiceProvider tdes, string filePath)\n    {\n        string base64Key = string.Empty;\n        string base64IV = string.Empty;\n\n        // Read the XML file\n        using (XmlTextReader reader = new XmlTextReader(filePath))\n        {\n            while (reader.Read())\n            {\n                if (reader.NodeType == XmlNodeType.Element)\n                {\n                    if (reader.Name == "Key")\n                    {\n                        base64Key = reader.ReadElementContentAsString();\n                    }\n                    else if (reader.Name == "IV")\n                    {\n                        base64IV = reader.ReadElementContentAsString();\n                    }\n                }\n            }\n        }\n\n        // Convert Base64 strings back to byte arrays\n        tdes.Key = Convert.FromBase64String(base64Key);\n        tdes.IV = Convert.FromBase64String(base64IV);\n    }\n}