using System;\nusing System.IO.Ports;\n\nclass Program\n{\n    static void Main()\n    {\n        SerialPort serialPort = new SerialPort("COM1", 9600, Parity.None, 8, StopBits.One);\n        serialPort.DataReceived += new SerialDataReceivedEventHandler(DataReceivedHandler);\n        serialPort.Open();\n        \n        Console.WriteLine("Listening for incoming calls...");\n        Console.ReadLine();\n        serialPort.Close();\n    }\n\n    private static void DataReceivedHandler(object sender, SerialDataReceivedEventArgs e)\n    {\n        SerialPort sp = (SerialPort)sender;\n        string inData = sp.ReadExisting();\n        Console.WriteLine($"Data Received: {inData}");\n\n        // Decode the caller ID info here\n        if (inData.Contains("NMBR"))\n        {\n            string phoneNumber = ExtractPhoneNumber(inData);\n            Console.WriteLine($"Incoming call from: {phoneNumber}");\n        }\n    }\n\n    private static string ExtractPhoneNumber(string data)\n    {\n        // Assuming the data contains "NMBR = 1234567890"\n        int start = data.IndexOf("NMBR") + 5;\n        int end = data.IndexOf("\r", start);\n        return data.Substring(start, end - start).Trim();\n    }\n}