using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\nusing System;\nusing System.IO;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        // Set up Chrome driver\n        var options = new ChromeOptions();\n        options.AddArgument("--headless"); // Run headless if desired\n        IWebDriver driver = new ChromeDriver(options);\n\n        try\n        {\n            // Navigate to the website\n            driver.Navigate().GoToUrl("http://sourcewebsite.com");\n\n            // Find and click the "Show All" checkbox\n            IWebElement showAllCheckbox = driver.FindElement(By.Id("ctl00_ContentPlaceHolder1_GenericWebUserControl_ShowAllCheckBox")); // Update the ID as per the actual element\n            if (!showAllCheckbox.Selected)\n            {\n                showAllCheckbox.Click();\n            }\n\n            // Wait for the page to load the new content, adjust as necessary\n            Thread.Sleep(5000);\n\n            // Get the page source\n            string pageSource = driver.PageSource;\n\n            // Save the page source to a file\n            File.WriteAllText("output.html", pageSource);\n\n            Console.WriteLine("Page has been saved to output.html");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n        finally\n        {\n            // Close the browser\n            driver.Quit();\n        }\n    }\n}