using System;\nusing System.Security.Cryptography;\nusing System.Xml;\n\npublic class TripleDESKeyStorage\n{\n    public void StoreKeyAndIV(TripleDESCryptoServiceProvider tdes, string filePath)\n    {\n        // Convert key and IV to Base64 strings\n        string base64Key = Convert.ToBase64String(tdes.Key);\n        string base64IV = Convert.ToBase64String(tdes.IV);\n\n        // Create an XML writer\n        using (XmlTextWriter writer = new XmlTextWriter(filePath, null))\n        {\n            writer.Formatting = Formatting.Indented;\n            writer.WriteStartDocument();\n            writer.WriteStartElement("TripleDES");\n\n            writer.WriteStartElement("Key");\n            writer.WriteString(base64Key);\n            writer.WriteEndElement();\n\n            writer.WriteStartElement("IV");\n            writer.WriteString(base64IV);\n            writer.WriteEndElement();\n\n            writer.WriteEndElement();\n            writer.WriteEndDocument();\n        }\n    }\n}