using System;\nusing System.Text.RegularExpressions;\n\npublic class ZipCodeValidator\n{\n    private static readonly Regex ZipPattern = new Regex(@"^\d{5}(-\d{4})?$");\n\n    public static bool IsValidUSZIP(string zip)\n    {\n        return ZipPattern.IsMatch(zip);\n    }\n\n    public static void Main()\n    {\n        Console.WriteLine(IsValidUSZIP("12345"));     // true\n        Console.WriteLine(IsValidUSZIP("12345-6789")); // true\n        Console.WriteLine(IsValidUSZIP("123456"));     // false\n    }\n}