protected void DataList1_ItemDataBound(object sender, DataListItemEventArgs e)\n{\n    if (e.Item.ItemType == ListItemType.Header || e.Item.ItemType == ListItemType.Footer)\n    {\n        if (e.Item.ItemType == ListItemType.Header)\n        {\n            string currentCountry = DataBinder.Eval(e.Item.DataItem, "Country").ToString();\n            if (ViewState["CurrentCountry"] == null || ViewState["CurrentCountry"].ToString() != currentCountry)\n            {\n                ViewState["CurrentCountry"] = currentCountry;\n                e.Item.Visible = true;\n            }\n            else\n            {\n                e.Item.Visible = false;\n            }\n        }\n    }\n}