using System;\nusing System.IO;\nusing System.Net;\nusing System.Web;\n\npublic class DownloadHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        string documentPathOnServerB = "http://serverB/documents/sample.pdf"; // Document URL on server B\n        string localPathOnServerA = context.Server.MapPath("~/temp/sample.pdf"); // Temporary local path on server A\n\n        // Copy file from server B to server A\n        using (WebClient webClient = new WebClient())\n        {\n            webClient.DownloadFile(documentPathOnServerB, localPathOnServerA);\n        }\n\n        // Serve the file for download\n        FileInfo file = new FileInfo(localPathOnServerA);\n        if (file.Exists)\n        {\n            context.Response.Clear();\n            context.Response.ContentType = "application/pdf"; // Adjust the content type as needed\n            context.Response.AddHeader("Content-Disposition", $"attachment; filename={file.Name}");\n            context.Response.AddHeader("Content-Length", file.Length.ToString());\n            context.Response.TransmitFile(file.FullName);\n            context.Response.Flush();\n\n            // Optionally delete the file after download\n            File.Delete(localPathOnServerA);\n        }\n        else\n        {\n            context.Response.StatusCode = 404;\n            context.Response.Write("File not found");\n        }\n\n        context.Response.End();\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}