public JsonResult GetPlansByYear(int rid, int year)\n{\n    var plans = (from bp in BenefitDataContext.GetContext.BenefitPlans\n                 where bp.Benefit.repository_id == rid && bp.Benefit.plan_year_val == year\n                 select bp.MphcPlan).Distinct();\n\n    var result = from p in plans\n                 select new SelectListItem\n                 {\n                     Text = p.plan_code + " - " + p.plan_desc,\n                     Value = p.plan_id.ToString()\n                 };\n\n    return Json(result, JsonRequestBehavior.AllowGet);\n}