using System;\nusing System.Web.UI;\n\npublic partial class ReorderControls : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Initial order of controls\n            PlaceHolder1.Controls.Add(new TextBox() { ID = "textbox1" });\n            PlaceHolder1.Controls.Add(new TextBox() { ID = "textbox2" });\n            PlaceHolder1.Controls.Add(new TextBox() { ID = "textbox3" });\n        }\n    }\n\n    protected void ButtonReorder_Click(object sender, EventArgs e)\n    {\n        // Get references to the controls\n        TextBox textbox1 = PlaceHolder1.FindControl("textbox1") as TextBox;\n        TextBox textbox2 = PlaceHolder1.FindControl("textbox2") as TextBox;\n        TextBox textbox3 = PlaceHolder1.FindControl("textbox3") as TextBox;\n\n        // Clear the current controls\n        PlaceHolder1.Controls.Clear();\n\n        // Add controls in new order based on user preferences\n        PlaceHolder1.Controls.Add(textbox3);\n        PlaceHolder1.Controls.Add(textbox2);\n        PlaceHolder1.Controls.Add(textbox1);\n    }\n}