using System;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private WebBrowser webBrowser;\n\n    public MainForm()\n    {\n        webBrowser = new WebBrowser();\n        webBrowser.Dock = DockStyle.Fill;\n        webBrowser.ScriptErrorsSuppressed = true;\n        webBrowser.NewWindow += new CancelEventHandler(webBrowser_NewWindow);\n        this.Controls.Add(webBrowser);\n        webBrowser.Navigate("http://yourwebsite.com");\n    }\n\n    private void webBrowser_NewWindow(object sender, CancelEventArgs e)\n    {\n        e.Cancel = true;\n        WebBrowser wb = (WebBrowser)sender;\n        HtmlElement link = wb.Document.ActiveElement;\n        string newUrl = link.GetAttribute("href");\n\n        if (!string.IsNullOrEmpty(newUrl))\n        {\n            webBrowser.Navigate(newUrl);\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MainForm());\n    }\n}