protected void Button_Click(object sender, EventArgs e)\n{\n    // Call the function to generate the PDF\n    string pdfPath = GeneratePdf();\n\n    // Register the script to open the PDF and print\n    ClientScript.RegisterStartupScript(this.GetType(), "PrintPDF", $"printPdf('{pdfPath}');", true);\n}\n\nprivate string GeneratePdf()\n{\n    // Logic to generate the PDF and save it to the server\n    string pdfPath = "/path/to/your/generated.pdf";\n    return pdfPath;\n}