public static bool operator ==(Region r1, Region r2)\n{\n    if (object.ReferenceEquals(r1, null) && object.ReferenceEquals(r2, null))\n    {\n        return true;\n    }\n    if (object.ReferenceEquals(r1, null) || object.ReferenceEquals(r2, null))\n    {\n        return false;\n    }\n    return r1.Cmr.CompareTo(r2.Cmr) == 0 && r1.Id == r2.Id;\n}\n\npublic static bool operator !=(Region r1, Region r2)\n{\n    return !(r1 == r2);\n}