using System.Data.SqlClient;\nusing System.Data.Odbc;\n\npublic void StreamBulkInsert(string sourceConnectionString, string destinationConnectionString, string sourceQuery, string destinationTable)\n{\n    using (OdbcConnection sourceConnection = new OdbcConnection(sourceConnectionString))\n    {\n        sourceConnection.Open();\n        using (OdbcCommand sourceCommand = new OdbcCommand(sourceQuery, sourceConnection))\n        using (OdbcDataReader reader = sourceCommand.ExecuteReader())\n        {\n            using (SqlConnection destinationConnection = new SqlConnection(destinationConnectionString))\n            {\n                destinationConnection.Open();\n                using (SqlBulkCopy bulkCopy = new SqlBulkCopy(destinationConnection))\n                {\n                    bulkCopy.DestinationTableName = destinationTable;\n                    bulkCopy.BatchSize = 5000;\n                    bulkCopy.BulkCopyTimeout = 600;\n                    bulkCopy.WriteToServer(reader);\n                }\n            }\n        }\n    }\n}