using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Data.Odbc;\n\npublic void BulkInsertData(string sourceConnectionString, string destinationConnectionString, string sourceQuery, string destinationTable)\n{\n    int chunkSize = 10000; // Number of records to process per chunk\n    int offset = 0;\n    bool moreData = true;\n\n    while (moreData)\n    {\n        using (OdbcConnection sourceConnection = new OdbcConnection(sourceConnectionString))\n        {\n            sourceConnection.Open();\n            string paginatedQuery = $"{sourceQuery} LIMIT {chunkSize} OFFSET {offset}"; // Adjust for your SQL dialect\n            OdbcDataAdapter adapter = new OdbcDataAdapter(paginatedQuery, sourceConnection);\n            DataTable dataTable = new DataTable();\n            adapter.Fill(dataTable);\n\n            if (dataTable.Rows.Count == 0)\n            {\n                moreData = false;\n                continue;\n            }\n\n            using (SqlConnection destinationConnection = new SqlConnection(destinationConnectionString))\n            {\n                destinationConnection.Open();\n                using (SqlBulkCopy bulkCopy = new SqlBulkCopy(destinationConnection))\n                {\n                    bulkCopy.DestinationTableName = destinationTable;\n                    bulkCopy.WriteToServer(dataTable);\n                }\n            }\n\n            offset += chunkSize;\n        }\n    }\n}