using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class ColorDialogExample : Form\n{\n    private Button button;\n    private ColorDialog colorDialog;\n\n    public ColorDialogExample()\n    {\n        button = new Button { Text = "Choose Color", Left = 50, Top = 50, Width = 100 };\n        button.Click += new EventHandler(Button_Click);\n        \n        colorDialog = new ColorDialog();\n        \n        this.Controls.Add(button);\n    }\n\n    private void Button_Click(object sender, EventArgs e)\n    {\n        Color initialColor = colorDialog.Color;\n        \n        if (colorDialog.ShowDialog() == DialogResult.OK)\n        {\n            Color newColor = colorDialog.Color;\n            if (initialColor != newColor)\n            {\n                // User has changed the color\n                MessageBox.Show("Color changed to: " + newColor.ToString());\n            }\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.Run(new ColorDialogExample());\n    }\n}