using System;\nusing System.Net.NetworkInformation;\n\nclass Program\n{\n    static void Main()\n    {\n        var networkInterfaces = NetworkInterface.GetAllNetworkInterfaces();\n\n        foreach (var ni in networkInterfaces)\n        {\n            Console.WriteLine($"Name: {ni.Name}");\n            Console.WriteLine($"Description: {ni.Description}");\n            Console.WriteLine($"Speed: {ni.Speed / 1_000_000} Mbps");\n\n            var ipv4Stats = ni.GetIPv4Statistics();\n            Console.WriteLine($"Bytes Sent: {ipv4Stats.BytesSent}");\n            Console.WriteLine($"Bytes Received: {ipv4Stats.BytesReceived}");\n            Console.WriteLine();\n        }\n    }\n}