using System;\nusing System.Data;\nusing System.Xml;\n\npublic class XmlToDataTable\n{\n    public static DataTable ConvertXmlDocumentToDataTable(XmlDocument xmlAPDP)\n    {\n        DataSet dsAPDP = new DataSet();\n        using (XmlNodeReader xmlReader = new XmlNodeReader(xmlAPDP))\n        {\n            dsAPDP.ReadXml(xmlReader);\n        }\n        \n        // Assuming the XML contains only one DataTable\n        DataTable dtAPDP = dsAPDP.Tables[0];\n        return dtAPDP;\n    }\n\n    public static void Main()\n    {\n        XmlDocument xmlAPDP = new XmlDocument();\n        xmlAPDP.Load("path_to_your_xml_file.xml"); // Load your XML content\n\n        DataTable dtAPDP = ConvertXmlDocumentToDataTable(xmlAPDP);\n        \n        // Print the DataTable to verify\n        foreach (DataRow row in dtAPDP.Rows)\n        {\n            foreach (DataColumn col in dtAPDP.Columns)\n            {\n                Console.Write(row[col] + "\t");\n            }\n            Console.WriteLine();\n        }\n    }\n}