using System.Data;\nusing System.Data.Common;\nusing Oracle.ManagedDataAccess.Client;\n\npublic class OracleHelper\n{\n    public static DbCommand CreateCommand(DbConnection conn, string commandText, params DbParameter[] parameters)\n    {\n        DbCommand cmd = conn.CreateCommand();\n        cmd.CommandText = commandText;\n        cmd.CommandType = CommandType.Text;\n\n        // Check if the command is an OracleCommand and set BindByName if applicable\n        if (cmd is OracleCommand oracleCommand)\n        {\n            oracleCommand.BindByName = true;\n        }\n\n        if (parameters != null)\n        {\n            foreach (var param in parameters)\n            {\n                cmd.Parameters.Add(param);\n            }\n        }\n\n        return cmd;\n    }\n}