using System;\n   using System.Web.UI.WebControls.WebParts;\n\n   public class EmailEncodingWebPart : WebPart\n   {\n       protected override void RenderContents(HtmlTextWriter writer)\n       {\n           string email = "jonny@example.com";\n           string encodedEmail = EncodeEmail(email);\n\n           writer.Write($"<a href=\"javascript:sendEmail('{encodedEmail}')\">Email Jonny</a>");\n       }\n\n       private string EncodeEmail(string email)\n       {\n           // Simple encoding logic (should match JavaScript encoding)\n           char[] chars = email.ToCharArray();\n           string encoded = "";\n           foreach (char c in chars)\n           {\n               encoded += ((int)c).ToString("X2");\n           }\n           return encoded;\n       }\n   }