using System;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    public MainForm()\n    {\n        // Set up a keyboard shortcut (e.g., Ctrl+M) to minimize the window\n        this.KeyPreview = true;\n        this.KeyDown += new KeyEventHandler(Form_KeyDown);\n\n        // Set up a context menu with a "Minimize" item\n        ContextMenu contextMenu = new ContextMenu();\n        MenuItem minimizeItem = new MenuItem("Minimize", new EventHandler(Minimize_Click));\n        contextMenu.MenuItems.Add(minimizeItem);\n        this.ContextMenu = contextMenu;\n    }\n\n    private void Form_KeyDown(object sender, KeyEventArgs e)\n    {\n        if (e.Control && e.KeyCode == Keys.M)\n        {\n            this.WindowState = FormWindowState.Minimized;\n        }\n    }\n\n    private void Minimize_Click(object sender, EventArgs e)\n    {\n        this.WindowState = FormWindowState.Minimized;\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MainForm());\n    }\n}