using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize the VirtualBox COM API\n        Type virtualBoxType = Type.GetTypeFromProgID("VirtualBox.VirtualBox");\n        dynamic virtualBox = Activator.CreateInstance(virtualBoxType);\n\n        // Access the Machines property via late binding\n        try\n        {\n            dynamic machines = virtualBox.Machines;\n            foreach (var machine in machines)\n            {\n                Console.WriteLine(machine.Name);\n            }\n        }\n        catch (COMException ex)\n        {\n            Console.WriteLine($"COM Exception: {ex.Message}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"General Exception: {ex.Message}");\n        }\n    }\n}