using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class CustomDateTimePicker : DateTimePicker\n{\n    public Color TextBackColor { get; set; }\n\n    public CustomDateTimePicker()\n    {\n        SetStyle(ControlStyles.UserPaint, true);\n        TextBackColor = SystemColors.Window;\n    }\n\n    protected override void OnPaint(PaintEventArgs e)\n    {\n        Rectangle rect = this.ClientRectangle;\n        using (Graphics g = this.CreateGraphics())\n        using (SolidBrush backColorBrush = new SolidBrush(TextBackColor))\n        using (SolidBrush textBrush = new SolidBrush(this.ForeColor))\n        {\n            g.FillRectangle(backColorBrush, rect);\n            TextRenderer.DrawText(g, this.Text, this.Font, rect, this.ForeColor, TextFormatFlags.Left);\n        }\n    }\n\n    protected override void OnCloseUp(EventArgs eventargs)\n    {\n        base.OnCloseUp(eventargs);\n        this.Invalidate();\n    }\n\n    protected override void OnValueChanged(EventArgs eventargs)\n    {\n        base.OnValueChanged(eventargs);\n        this.Invalidate();\n    }\n}