using System;\nusing Tweetinvi;\nusing Tweetinvi.Models;\nusing Tweetinvi.Parameters;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Set up your Twitter API credentials\n        var userClient = new TwitterClient("CONSUMER_KEY", "CONSUMER_SECRET", "ACCESS_TOKEN", "ACCESS_TOKEN_SECRET");\n\n        // Fetch a user's timeline\n        var user = userClient.Users.GetUserAsync("TwitterUser").Result;\n        var timelineParameters = new GetUserTimelineParameters(user)\n        {\n            PageSize = 10\n        };\n        var tweets = userClient.Timelines.GetUserTimelineAsync(timelineParameters).Result;\n\n        // Display the tweets\n        foreach (var tweet in tweets)\n        {\n            Console.WriteLine($"{tweet.CreatedAt}: {tweet.FullText}");\n        }\n    }\n}