using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        var comObject = new SomeComObject();\n        \n        try\n        {\n            // Use the COM object\n        }\n        finally\n        {\n            // Release the COM object\n            if (comObject != null && Marshal.IsComObject(comObject))\n            {\n                Marshal.ReleaseComObject(comObject);\n            }\n        }\n    }\n}