using System;\nusing System.Runtime.InteropServices;\n\npublic class CodeGenerator\n{\n    [DllImport("CreateCodeDLL.dll", CallingConvention = CallingConvention.StdCall, CharSet = CharSet.Ansi)]\n    public static extern IntPtr CreateCode(string SerialID,\n                                           ushort StartDateOfYear,\n                                           ushort YearOfStartDate,\n                                           ushort YearOfEndDate,\n                                           ushort DatePeriod,\n                                           byte CodeType,\n                                           byte RecordNumber,\n                                           byte StartHour,\n                                           byte EndHour);\n\n    public static void Main()\n    {\n        // The Inputs\n        string serialID = "92F00000B4FBE";\n        ushort StartDateOfYear = 20;\n        ushort YearOfStartDate = 2009;\n        ushort YearOfEndDate = 2009;\n        ushort DatePeriod = 7;\n        byte CodeType = 1;\n        byte RecordNumber = 0;\n        byte StartHour = 15;\n        byte EndHour = 14;\n\n        // The DLL call\n        IntPtr codePtr = CreateCode(serialID, StartDateOfYear, YearOfStartDate, YearOfEndDate, DatePeriod, CodeType, RecordNumber, StartHour, EndHour);\n\n        // Check if the pointer is not null\n        if (codePtr != IntPtr.Zero)\n        {\n            // Take the pointer and extract the code in a string\n            string code = Marshal.PtrToStringAnsi(codePtr);\n\n            // Output the result\n            Console.WriteLine("Generated Code: " + code);\n        }\n        else\n        {\n            Console.WriteLine("Failed to generate code.");\n        }\n    }\n}