using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string content = "the brown fox jumped over <b>the</b> lazy dog over there";\n        string keyword = "the";\n        string replacement = $"<span style=\"background-color:yellow;\">{keyword}</span>";\n\n        // Regex to match whole words outside of HTML tags\n        string pattern = $@"(?<!<[^>]*)\b{Regex.Escape(keyword)}\b(?![^<]*>)";\n\n        string result = Regex.Replace(content, pattern, replacement, RegexOptions.IgnoreCase);\n        \n        Console.WriteLine(result);\n    }\n}