using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class EncryptionHelper\n{\n    public static byte[] GenerateSalt(int length)\n    {\n        using (var rng = new RNGCryptoServiceProvider())\n        {\n            byte[] salt = new byte[length];\n            rng.GetBytes(salt);\n            return salt;\n        }\n    }\n\n    public static byte[] GenerateKey(string password, byte[] salt, int iterations = 10000, int keySize = 32)\n    {\n        using (var rfc2898 = new Rfc2898DeriveBytes(password, salt, iterations))\n        {\n            return rfc2898.GetBytes(keySize);\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string password = "your-password";\n        byte[] salt = GenerateSalt(16); // Generate a 16-byte salt\n\n        // Store the salt securely, e.g., as part of the encrypted data\n        byte[] key = GenerateKey(password, salt);\n\n        // Print the key as a base64 string for demonstration purposes\n        Console.WriteLine(Convert.ToBase64String(key));\n    }\n}