using System;\nusing Microsoft.SharePoint;\n\nnamespace CustomEventReceiver\n{\n    public class ItemAddedEventReceiver : SPItemEventReceiver\n    {\n        public override void ItemAdded(SPItemEventProperties properties)\n        {\n            base.ItemAdded(properties);\n\n            // Run with elevated privileges to ensure permissions are not an issue\n            SPSecurity.RunWithElevatedPrivileges(delegate()\n            {\n                using (SPSite site = new SPSite(properties.SiteId))\n                {\n                    using (SPWeb web = site.OpenWeb(properties.RelativeWebUrl))\n                    {\n                        SPListItem item = properties.ListItem;\n                        web.AllowUnsafeUpdates = true;\n\n                        // Set the "Created By" field to the appropriate user\n                        SPUser targetUser = web.EnsureUser("DOMAIN\\username"); // Replace with logic to get the appropriate user\n                        item["Author"] = targetUser;\n                        item.Update();\n\n                        web.AllowUnsafeUpdates = false;\n                    }\n                }\n            });\n        }\n    }\n}