using System;\nusing Oracle.ManagedDataAccess.Client;\nusing Oracle.ManagedDataAccess.Types;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string connString = "User Id=<YourUser>;Password=<YourPassword>;Data Source=<YourDataSource>";\n\n        using (OracleConnection conn = new OracleConnection(connString))\n        {\n            conn.Open();\n\n            // Create and configure the command\n            using (OracleCommand cmd = new OracleCommand("SELECT * FROM your_table", conn))\n            {\n                // Create and configure the dependency\n                OracleDependency dep = new OracleDependency(cmd);\n                dep.OnChange += new OnChangeEventHandler(OnDatabaseChange);\n                \n                // Execute the command\n                using (OracleDataReader reader = cmd.ExecuteReader())\n                {\n                    // Read the data\n                    while (reader.Read())\n                    {\n                        Console.WriteLine(reader[0]);\n                    }\n                }\n            }\n        }\n\n        // Keep the application running\n        Console.WriteLine("Press any key to exit...");\n        Console.ReadKey();\n    }\n\n    static void OnDatabaseChange(object sender, OracleNotificationEventArgs args)\n    {\n        Console.WriteLine("Database change detected:");\n        foreach (OracleNotificationRowChange change in args.Details)\n        {\n            Console.WriteLine($"Resource: {change.ResourceName}");\n            Console.WriteLine($"Operation: {change.Operation}");\n        }\n    }\n}