using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string";\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.InfoMessage += new SqlInfoMessageEventHandler(OnInfoMessage);\n            connection.Open();\n\n            SqlCommand command = new SqlCommand("SampleProcedure", connection);\n            command.CommandType = System.Data.CommandType.StoredProcedure;\n            command.ExecuteNonQuery();\n        }\n    }\n\n    private static void OnInfoMessage(object sender, SqlInfoMessageEventArgs e)\n    {\n        Console.WriteLine("InfoMessage: " + e.Message);\n    }\n}