using System;\nusing System.Globalization;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "24/5/2009 14:00";\n        DateTime parsedDate = DateTime.ParseExact(input, "d/M/yyyy H:mm", CultureInfo.InvariantCulture);\n        string result = parsedDate.ToString("M/d/yyyy h:mm tt", CultureInfo.InvariantCulture);\n        Console.WriteLine(result);  // Output: 5/24/2009 2:00 PM\n    }\n}