void PopulateTreeView(TreeNode parentNode)\n{\n    // Fetch nodes from the database where ParentID matches the parentNode ID\n    var nodes = GetNodesFromDatabase(parentNode?.NodeID);\n\n    foreach (var node in nodes)\n    {\n        TreeNode treeNode = new TreeNode(node.NodeName);\n        treeNode.Tag = node.NodeID;\n\n        // Add dummy node if the current node has children\n        if (node.HasChildren)\n        {\n            treeNode.Nodes.Add(new TreeNode("Loading..."));\n        }\n\n        if (parentNode == null)\n        {\n            treeView.Nodes.Add(treeNode);\n        }\n        else\n        {\n            parentNode.Nodes.Add(treeNode);\n        }\n    }\n}