using System.Net;\nusing System.Net.Mail;\n\nSmtpClient client = new SmtpClient("smtp.server.com")\n{\n    Credentials = new NetworkCredential("user", "password"),\n    EnableSsl = true,  // Enable SSL if required\n    Port = 587        // Set the port if needed (default is 25)\n};\n\nMailAddress mailFrom = new MailAddress("mymail@server.com", "Your Name");\nMailAddress mailTo = new MailAddress("yourmail@server.com");\nMailMessage message = new MailMessage\n{\n    From = mailFrom,\n    Subject = "test message",\n    SubjectEncoding = System.Text.Encoding.UTF8,\n    Body = "This is a test message.",\n    BodyEncoding = System.Text.Encoding.UTF8,\n    IsBodyHtml = true  // Use HTML for better formatting\n};\n\nmessage.To.Add(mailTo);\nmessage.ReplyToList.Add(new MailAddress("mymail@server.com"));\nmessage.Headers.Add("X-Priority", "3");  // Normal priority\nmessage.Headers.Add("X-MSMail-Priority", "Normal");\nmessage.Headers.Add("X-Mailer", "Microsoft Outlook 15.0");\n\ntry\n{\n    client.Send(message);\n    Console.WriteLine("Email sent successfully.");\n}\ncatch (SmtpException ex)\n{\n    Console.WriteLine("SmtpException has occurred: " + ex.Message);\n}\ncatch (Exception ex)\n{\n    Console.WriteLine("Exception has occurred: " + ex.Message);\n}