public override void Input0_ProcessInputRow(Input0Buffer Row)\n{\n    // Example: Assume input has a single column named "InputColumn" of type string\n    string inputRecord = Row.InputColumn;\n\n    // Define the fixed length (326 bytes)\n    int fixedLength = 326;\n\n    // Pad the input record if it's shorter than the fixed length\n    if (inputRecord.Length < fixedLength)\n    {\n        inputRecord = inputRecord.PadRight(fixedLength, ' ');\n    }\n\n    // Assign the padded or original record to the output column\n    Row.OutputColumn = inputRecord;\n}