using System;\nusing System.Drawing;\nusing System.Windows.Forms;\nusing Spire.Pdf;\n\nnamespace DisplayPDF\n{\n    public partial class Form1 : Form\n    {\n        public Form1()\n        {\n            InitializeComponent();\n            LoadPdf(@"path\to\your\file.pdf");\n        }\n\n        private void LoadPdf(string pdfPath)\n        {\n            // Load the PDF document\n            PdfDocument pdfDocument = new PdfDocument();\n            pdfDocument.LoadFromFile(pdfPath);\n\n            // Get the first page and convert it to an image\n            Image pageImage = pdfDocument.SaveAsImage(0);\n\n            // Display the image in a PictureBox\n            PictureBox pictureBox = new PictureBox\n            {\n                Dock = DockStyle.Fill,\n                Image = pageImage,\n                SizeMode = PictureBoxSizeMode.StretchImage\n            };\n            this.Controls.Add(pictureBox);\n        }\n    }\n}