using System.Data.OleDb;\n\npublic void CompactAccessDatabase(string sourceDatabase, string destinationDatabase)\n{\n    string connectionString = "Provider=Microsoft.ACE.OLEDB.12.0;Data Source=" + sourceDatabase + ";Jet OLEDB:Engine Type=5";\n\n    var catalog = new ADOX.Catalog();\n    catalog.Create("Provider=Microsoft.ACE.OLEDB.12.0;Data Source=" + destinationDatabase + ";Jet OLEDB:Engine Type=5");\n\n    using (var sourceConnection = new OleDbConnection(connectionString))\n    {\n        sourceConnection.Open();\n    }\n\n    using (var destinationConnection = new OleDbConnection("Provider=Microsoft.ACE.OLEDB.12.0;Data Source=" + destinationDatabase))\n    {\n        destinationConnection.Open();\n    }\n}