using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("YourDllName.dll", CallingConvention = CallingConvention.Cdecl, SetLastError = true)]\n    public static extern int YourFunction(int param);\n\n    static void Main()\n    {\n        try\n        {\n            int result = YourFunction(42);\n            Console.WriteLine(result);\n        }\n        catch (Exception ex)\n        {\n            int errorCode = Marshal.GetLastWin32Error();\n            Console.WriteLine($"Error: {ex.Message}, Code: {errorCode}");\n        }\n    }\n}