using System.Configuration;\nusing System.Web.Configuration;\n\nclass Program\n{\n    static void Main()\n    {\n        // Specify the name of the section to encrypt\n        string sectionName = "appSettings";\n        \n        // Open the configuration file and get the section\n        Configuration config = ConfigurationManager.OpenExeConfiguration(ConfigurationUserLevel.None);\n        ConfigurationSection section = config.GetSection(sectionName);\n        \n        if (section != null && !section.SectionInformation.IsProtected)\n        {\n            // Protect the section\n            section.SectionInformation.ProtectSection("RsaProtectedConfigurationProvider");\n            \n            // Save the changes\n            section.SectionInformation.ForceSave = true;\n            config.Save(ConfigurationSaveMode.Full);\n            \n            Console.WriteLine($"{sectionName} section encrypted successfully.");\n        }\n        else\n        {\n            Console.WriteLine($"{sectionName} section is already encrypted or not found.");\n        }\n    }\n}