using System;\nusing System.Collections.Generic;\nusing System.IO;\n\npublic class DelimitedFileWriter\n{\n    public void WriteToFile<T>(string filePath, IEnumerable<T> records, string delimiter = ",")\n    {\n        using (var writer = new StreamWriter(filePath))\n        {\n            foreach (var record in records)\n            {\n                var properties = record.GetType().GetProperties();\n                var values = new List<string>();\n                foreach (var prop in properties)\n                {\n                    var value = prop.GetValue(record)?.ToString() ?? string.Empty;\n                    values.Add(value);\n                }\n                var line = string.Join(delimiter, values);\n                writer.WriteLine(line);\n            }\n        }\n    }\n}\n\npublic class Record\n{\n    // Example record properties\n    public string Field1 { get; set; }\n    public string Field2 { get; set; }\n    // Add more fields as needed\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var records = new List<Record>\n        {\n            new Record { Field1 = "Value1", Field2 = "Value2" },\n            // Add more records as needed\n        };\n\n        var writer = new DelimitedFileWriter();\n        writer.WriteToFile("output.csv", records);\n    }\n}