using System;\nusing Microsoft.Win32;\nusing System.Security.Principal;\n\nclass Program\n{\n    static void Main()\n    {\n        SetDateFormat("dd/MM/yyyy");\n    }\n\n    static void SetDateFormat(string dateFormat)\n    {\n        try\n        {\n            // Current user settings\n            using (RegistryKey currentUserKey = Registry.CurrentUser.OpenSubKey(@"Control Panel\International", true))\n            {\n                if (currentUserKey != null)\n                {\n                    currentUserKey.SetValue("sShortDate", dateFormat, RegistryValueKind.String);\n                    currentUserKey.SetValue("sLongDate", "dddd, dd MMMM yyyy", RegistryValueKind.String); // Example long date format\n                }\n            }\n\n            // Default user settings\n            using (RegistryKey defaultUserKey = Registry.Users.OpenSubKey(@".DEFAULT\Control Panel\International", true))\n            {\n                if (defaultUserKey != null)\n                {\n                    defaultUserKey.SetValue("sShortDate", dateFormat, RegistryValueKind.String);\n                    defaultUserKey.SetValue("sLongDate", "dddd, dd MMMM yyyy", RegistryValueKind.String); // Example long date format\n                }\n            }\n\n            Console.WriteLine("Date format set successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error setting date format: " + ex.Message);\n        }\n    }\n}