public void SaveDefaultValues(MyEntity defaultValues)\n{\n    using (var context = new MyDbContext())\n    {\n        var existingDefaults = context.MyEntities.Find(defaultValues.Id);\n        if (existingDefaults != null)\n        {\n            context.Entry(existingDefaults).CurrentValues.SetValues(defaultValues);\n        }\n        else\n        {\n            context.MyEntities.Add(defaultValues);\n        }\n        context.SaveChanges();\n    }\n}