using System;\nusing System.IO;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        string url = "http://example.com"; // Replace with your URL\n        int bz = 1024; // Buffer size\n        byte[] buffer = new byte[bz];\n\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n        CookieContainer cookieContainer = new CookieContainer();\n        request.CookieContainer = cookieContainer;\n        HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n        Stream responsestream = response.GetResponseStream();\n\n        // Extract cookies from the response\n        CookieCollection cookies = response.Cookies;\n        StringBuilder cookieHeader = new StringBuilder();\n        foreach (Cookie cookie in cookies)\n        {\n            cookieHeader.Append($"{cookie.Name}={cookie.Value}; ");\n        }\n\n        // Prepare the HTTP response headers\n        StringBuilder httpResponseHeader = new StringBuilder();\n        httpResponseHeader.AppendLine("HTTP/1.1 200 OK");\n        httpResponseHeader.AppendLine("Content-Type: text/html");\n        if (cookieHeader.Length > 0)\n        {\n            httpResponseHeader.AppendLine($"Set-Cookie: {cookieHeader.ToString().TrimEnd(' ', ';')}");\n        }\n        httpResponseHeader.AppendLine("Connection: close");\n        httpResponseHeader.AppendLine();\n\n        // Convert the headers to byte array\n        byte[] headerBytes = Encoding.ASCII.GetBytes(httpResponseHeader.ToString());\n\n        // Assuming `soket` is your connected socket\n        Socket soket = new Socket(AddressFamily.InterNetwork, SocketType.Stream, ProtocolType.Tcp);\n        // Connect to your client\n        // soket.Connect(new IPEndPoint(IPAddress.Parse("client_ip"), clientPort)); // Uncomment and set client IP and port\n\n        // Send headers\n        soket.Send(headerBytes);\n\n        // Send response body\n        int rebu = responsestream.Read(buffer, 0, bz);\n        while (rebu != 0)\n        {\n            soket.Send(buffer, rebu, SocketFlags.None);\n            rebu = responsestream.Read(buffer, 0, bz);\n        }\n\n        // Close the socket\n        soket.Shutdown(SocketShutdown.Both);\n        soket.Close();\n    }\n}